import React from "react";
import ReactDOM from "react-dom/client";

import "./index.css";

function BookList() {
  return (
    <section className="booklist">
      <Book />
      <Book />
      <Book />
      <Book />
    </section>
  );
}

const Book = () => {
  return (
    <article className="book">
      <Image />
      <Title />
      <Author />
    </article>
  );
};

const Image = () => (
  <img
    src="https://m.media-amazon.com/images/I/91bYsX41DVL._AC_UY436_FMwebp_QL65_.jpg"
    alt="Atomic Habits"
  />
);
const Title = () => {
  return (
    <h2 style={{ color: "#FF0000", fontSize: "0.75rem", marginTop: "0.5rem" }}>
      Atomic Habits
    </h2>
  );
};

const Author = () => (
  <h4 style={{ color: "#617d98", fontSize: "0.75rem", marginTop: "0.5rem" }}>
    James Clear
  </h4>
);

const root = ReactDOM.createRoot(document.getElementById("root"));
root.render(<BookList />);
